package Arrayinjava;

public class types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [] marks = new int[5];
		marks [0] = 96;
		marks [1] = 98;
		marks [2] = 86;
		marks [3] = 81;
		marks [4] = 76;
		
		System.out.println(marks[0]);
		System.out.println(marks[3]);
		System.out.println(marks[1]);
		
		System.out.println("marks length is :"+marks.length);
		
		int [] rates = {96,78,56,85,77,89};
		
		System.out.println(rates[5]);
		System.out.println("rates length is:"+rates.length);
	}

}
